"use strict";(()=>{document.addEventListener("DOMContentLoaded",()=>{let e=document.getElementById("contact-form"),t=document.getElementById("contact-form-status");if(!e||!t)return;async function s(o){o.preventDefault();let a=new FormData(o.target);t.textContent="Envoi en cours...",t.style.color="var(--fg)";try{let r=await fetch(o.target.action,{method:e.method,body:a,headers:{Accept:"application/json"}});if(r.ok)t.textContent="Merci pour votre message ! Nous vous r\xE9pondrons bient\xF4t.",t.style.color="var(--accent2)",e.reset();else{let n=await r.json();Object.hasOwn(n,"errors")?t.textContent=n.errors.map(c=>c.message).join(", "):t.textContent="Oops! Une erreur s'est produite lors de l'envoi du formulaire.",t.style.color="#ff4444"}}catch{t.textContent="Oops! Une erreur r\xE9seau s'est produite.",t.style.color="#ff4444"}}e.addEventListener("submit",s)});})();
